mapper.select:
		SELECT

            ,(CASE  WHEN (SELECT COUNT(1) FROM sys_area WHERE parent_id = a.id) >0 THEN 1 ELSE 0 END) AS "isTreeLeaf"
        FROM sys_area a


                and a.name LIKE

                    '%,'||#{name}||',%'
                    CONCAT('%', #{name}, '%')


             AND a.area_level = #{areaLevel} 
             AND a.parent_id = #{parentId} 
             AND a.zip_code = #{zipCode} 
             AND a.area_code = #{areaCode}

		SELECT

        ,(CASE  WHEN (SELECT COUNT(1) FROM sys_area WHERE parent_id = a.id) >0 THEN 1 ELSE 0 END) AS "isTreeLeaf"
		FROM sys_area a



                AND a.name LIKE

                    '%,'||#{sysArea.name}||',%'
                    CONCAT('%', #{sysArea.name}, '%')


             AND a.area_level = #{sysArea.areaLevel} 
             AND a.parent_id = #{sysArea.parentId} 
             AND a.zip_code = #{sysArea.zipCode} 
             AND a.area_code = #{sysArea.areaCode}
