mapper.select:

WHERE c.del_flag = '0'

    AND c.parent_id = #{parentId}


    AND ((FIND_IN_SET(#{id},c.parent_ids))
    AND (('${id}' = ANY(STRING_TO_ARRAY(c.parent_ids, ',')))
    AND (INSTR(','||c.parent_ids||',',',${id},') > 0
    AND (CHARINDEX(','+'${id}'+',', ','+c.parent_ids+',') > 0
    OR (c.id = '${id}' AND c.del_flag = '0'))


    AND c.name LIKE

        '%,'||#{name}||',%'
        CONCAT('%', #{name}, '%')



    AND c.status = #{status}


    AND c.type = #{type}


    AND c.id IN

      #{id}




    AND ${sql_filter}
