mapper.select:













    WHERE m.cs IN

        #{code}


ORDER BY m.parent_id, m.order_num













WHERE m.parent_id = #{parentId} AND m.hide = 0
AND m.cs = #{modules}
ORDER BY m.order_num ASC

LEFT JOIN sys_role_menu rm ON m.id = rm.menu_id LEFT JOIN sys_user_role ur ON rm.role_id = ur.role_id LEFT JOIN sys_role ro ON ur.role_id = ro.id WHERE ur.user_id = #{userId}
AND m.cs IN
#{code}
ORDER BY m.parent_id, m.order_num

SELECT DISTINCT m.id, m.parent_id, m.name, m.url, m.hide, m.is_new, NVL(m.perms,'') AS perms, IFNULL(m.perms,'') AS perms, COALESCE(m.perms,'') AS perms, ISNULL(m.perms,'') AS perms, m.target,m.type, m.icon, m.order_num, m.create_time FROM sys_menu m LEFT JOIN sys_role_menu rm ON m.id = rm.menu_id LEFT JOIN sys_user_role ur ON rm.role_id = ur.role_id LEFT JOIN sys_role ro ON ur.role_id = ro.id WHERE ur.user_id = #{userId}
AND m.name LIKE
'%,'||#{name}||',%' CONCAT('%', #{name}, '%')
AND m.hide = #{hide}
ORDER BY m.parent_id, m.order_num
