mapper.select:
SELECT

FROM sys_msg_push a

     AND a.msg_title = #{msgTitle} 
     AND a.msg_level = #{msgLevel} 
     AND a.is_comments = #{isComments} 
     AND a.send_user_name = #{sendUserName} 
     AND a.send_user_id = #{sendUserId}

SELECT

, d.is_read AS isRead
FROM sys_msg_push a
LEFT JOIN (SELECT * FROM sys_msg_push_user u
UNION ALL SELECT * FROM sys_msg_cc_user c ) d on a.id = d.msg_id

     AND a.msg_title = #{msgTitle} 
     AND a.msg_level = #{msgLevel} 
     AND a.is_comments = #{isComments} 
     AND a.send_user_name = #{sendUserName} 
     AND a.send_user_id = #{sendUserId} 
     AND d.user_id = #{userId} 
     AND d.is_read = #{isRead}

SELECT

FROM sys_msg_push a

     AND a.msg_title = #{sysMsgPush.msgTitle} 
     AND a.send_user_id = #{sysMsgPush.sendUserId} 
     AND a.msg_level = #{sysMsgPush.msgLevel} 
     AND a.is_comments = #{sysMsgPush.isComments} 
     AND a.send_user_name = #{sysMsgPush.sendUserName} 

        AND ${sql_filter}

SELECT

, d.is_read AS isRead
FROM sys_msg_push a
LEFT JOIN (SELECT * FROM sys_msg_push_user u
UNION ALL SELECT * FROM sys_msg_cc_user c ) d on a.id = d.msg_id

     AND a.msg_title = #{sysMsgPush.msgTitle} 
     AND a.send_user_id = #{sysMsgPush.sendUserId} 
     AND a.msg_level = #{sysMsgPush.msgLevel} 
     AND a.is_comments = #{sysMsgPush.isComments} 
     AND a.send_user_name = #{sysMsgPush.sendUserName} 
     AND d.user_id = #{sysMsgPush.userId} 

        AND ${sql_filter}
