mapper.update:
UPDATE sys_user
name = #{name,jdbcType=VARCHAR}, email = #{email,jdbcType=VARCHAR}, mobile = #{mobile,jdbcType=VARCHAR}, status = #{status,jdbcType=INTEGER}, comp_id = #{compId,jdbcType=INTEGER}, dept_id = #{deptId,jdbcType=INTEGER}, card_id = #{cardId,jdbcType=VARCHAR}, avatar = #{avatar,jdbcType=VARCHAR}, update_by = #{updateBy,jdbcType=VARCHAR}, remark = #{remark,jdbcType=VARCHAR}, update_time = SYSDATE() update_time = CLOCK_TIMESTAMP() update_time = GETDATE() update_time = TO_TIMESTAMP(TO_CHAR(SYSDATE,'YYYY-MM-DD')||TO_CHAR(SYSDATE,'hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss')
WHERE id = #{id}

UPDATE sys_user
password = #{password}, salt = #{salt}, pwd_security_level = #{pwdSecurityLevel}, update_time = SYSDATE() update_time = CLOCK_TIMESTAMP() update_time = GETDATE() update_time = TO_TIMESTAMP(TO_CHAR(SYSDATE,'YYYY-MM-DD')||TO_CHAR(SYSDATE,'hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss')
WHERE id = #{id}

UPDATE sys_user
avatar = #{avatar,jdbcType=VARCHAR}, update_time = SYSDATE() update_time = CLOCK_TIMESTAMP() update_time = GETDATE() update_time = TO_TIMESTAMP(TO_CHAR(SYSDATE,'YYYY-MM-DD')||TO_CHAR(SYSDATE,'hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss')
WHERE id = #{id}

mapper.select:
		SELECT DISTINCT rm.menu_id FROM sys_user_role ur
        LEFT JOIN sys_role_menu rm ON ur.role_id = rm.role_id
		WHERE ur.user_id = #{id}

