mapper.update:
update sys_user
name = #{name,jdbcType=VARCHAR}, email = #{email,jdbcType=VARCHAR}, mobile = #{mobile,jdbcType=VARCHAR}, status = #{status,jdbcType=INTEGER}, comp_id = #{compId,jdbcType=INTEGER}, dept_id = #{deptId,jdbcType=INTEGER}, card_id = #{cardId,jdbcType=VARCHAR}, avatar = #{avatar,jdbcType=VARCHAR}, update_by = #{updateBy,jdbcType=VARCHAR}, remark = #{remark,jdbcType=VARCHAR}, update_time = sysdate()
where user_id = #{userId}

update sys_user
password = #{password}, salt = #{salt}, pwd_security_level = #{pwdSecurityLevel}, pwd_update_date = sysdate()
where user_id = #{userId}

update sys_user
avatar = #{avatar,jdbcType=VARCHAR}, update_time = sysdate()
where user_id = #{userId}

mapper.select:
select distinct rm.menu_id from sys_user_role ur
	LEFT JOIN sys_role_menu rm on ur.role_id = rm.role_id
where ur.user_id = #{userId}

where u.del_flag = '0'
AND u.username like '%,'||#{username}||',%' concat('%', #{username}, '%')
AND u.status = #{status}
AND u.mobile like '%,'||#{mobile}||',%' concat('%', #{mobile}, '%')
AND u.email like '%,'||#{email}||',%' concat('%', #{email}, '%')
AND u.comp_id = #{compId}
and ${sql_filter}
group by u.user_id,u.username, u.name,u.password,u.salt,u.email,u.mobile,u.status,u.card_id,u.create_time,u.dept_id,u.comp_id,c.name group by u.user_id

where u.del_flag = '0'
AND u.username like '%,'||#{username}||',%' concat('%', #{username}, '%')
AND u.status = #{status}
AND u.mobile like '%,'||#{mobile}||',%' concat('%', #{mobile}, '%')
AND u.email like '%,'||#{email}||',%' concat('%', #{email}, '%')
AND u.comp_id = #{compId}
and ${sql_filter}
group by u.user_id,u.username, u.name,u.password,u.salt,u.email,u.mobile,u.status,u.card_id,u.create_time,u.dept_id,u.comp_id,c.name group by u.user_id

where u.del_flag = '0' and r.role_id = #{roleId}
AND u.username like '%,'||#{username}||',%' concat('%', #{username}, '%')
AND u.status = #{status}
AND u.mobile like '%,'||#{mobile}||',%' concat('%', #{mobile}, '%')
AND u.email like '%,'||#{email}||',%' concat('%', #{email}, '%')
AND u.comp_id = #{compId}
and ${sql_filter}

select distinct u.user_id, u.dept_id, u.username, u.name, u.email, u.avatar, u.mobile, u.status,u.create_time, nvl(c.name,'总公司') as comp_name ifnull(c.name,"总公司") as comp_name from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id left join sys_comp c on u.comp_id = c.comp_id where (r.role_id != #{roleId} or r.role_id IS NULL) and u.user_id not in (select u.user_id from sys_user u inner join sys_user_role ur on u.user_id = ur.user_id and ur.role_id = #{roleId})
AND u.username like '%,'||#{username}||',%' concat('%', #{username}, '%')
AND u.mobile like '%,'||#{mobile}||',%' concat('%', #{mobile}, '%')
AND u.email like concat('%', #{email}, '%')
AND u.comp_id = #{compId}
and ${sql_filter}

