(function ($) {
    'use strict';

    $.fn.bootstrapTable.locales['zh-CN'] = {
        formatLoadingMessage: function () {
            return  $.i18n.prop('数据正在努力加载中,请稍后');
        },
        formatRecordsPerPage: function (pageNumber) {
            return $.i18n.prop('{0} 条记录每页',pageNumber);
        },
        formatShowingRows: function (pageFrom, pageTo, totalRows) {
            return $.i18n.prop('第 {0} 到 {1} 条，共 {2} 条记录。',pageFrom,pageTo,totalRows);
        },
        formatSearch: function () {
            return  $.i18n.prop('查询');
        },
        formatCheckNumber: function (number) {
            return $.i18n.prop('勾选总数:{0}',number);
        },
        formatPageGo: function () {
            return $.i18n.prop('跳转');
        },
        formatNoMatches: function () {
            return  '<div class="table-empty"><div class="table-empty__image" style="height: 50px;"><img src="data:image/svg+xml;base64," alt=""></div><p class="table-empty__desc">'+$.i18n.prop('暂无数据')+'</p></div>';
        },
        formatPaginationSwitch: function () {
            return  $.i18n.prop('隐藏/显示分页');
        },
        formatRefresh: function () {
            return $.i18n.prop('刷新');
        },
        formatToggle: function () {
            return $.i18n.prop('切换');
        },
        formatColumns: function () {
            return $.i18n.prop('列');
        },
        formatExport: function () {
            return $.i18n.prop('导出数据');
        },
        formatClearFilters: function () {
            return $.i18n.prop('清空过滤');
        }
    };

    $.extend($.fn.bootstrapTable.defaults, $.fn.bootstrapTable.locales['zh-CN']);

})(jQuery);
