mapper.select:
    SELECT t.table_id, t.table_name,t.parent_id,t.parent_name,t.menu_name,t.module_codes,t.menu_icon,t.menu_target,t.menu_order
	FROM gen_table t
	where t.table_id = #{tableId}

mapper.update:
update gen_table
table_name = #{tableName}, table_comment = #{tableComment}, class_name = #{className}, function_author = #{functionAuthor}, tpl_category = #{tplCategory}, package_name = #{packageName}, module_name = #{moduleName}, business_name = #{businessName}, function_name = #{functionName}, run_path = #{runPath}, is_del = #{isDel}, db_type = #{dbType}, actions_type = #{actionsType}, is_cover = #{isCover}, options = #{options}, parent_id = #{parentId}, menu_order = #{menuOrder}, parent_name = #{parentName}, menu_name = #{menuName}, module_codes = #{moduleCodes}, menu_icon = #{menuIcon}, menu_target = #{menuTarget}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where table_id = #{tableId}
