mapper.select:
SELECT
,(CASE WHEN (SELECT COUNT(1) FROM example_tree WHERE parent_id = a.id AND del_flag = '0' ) >0 THEN 1 ELSE 0 END) AS "isTreeLeaf" FROM example_tree a
a.del_flag = '0' AND a.parent_id = #{parentId}
AND a.name LIKE CONCAT('%',#{name},'%')
AND a.status = #{status}

SELECT

,(CASE  WHEN (SELECT COUNT(1) FROM example_tree WHERE parent_id = a.id  AND del_flag = '0' ) >0 THEN 1 ELSE 0 END) AS "isTreeLeaf"
FROM example_tree a

    a.del_flag = '0'
     AND a.parent_id = #{exampleAsyncTree.parentId} 

        AND a.name LIKE
        CONCAT('%',#{exampleAsyncTree.name},'%')

     AND a.status = #{exampleAsyncTree.status} 

        AND ${sql_filter}
