        CONCAT('%',#{exampleTest.name},'%')

     AND a.email = #{exampleTest.email} 
     AND a.phone = #{exampleTest.phone} 
     AND a.sex = #{exampleTest.sex} 
     AND a.age = #{exampleTest.age} 
